import * as vscode from 'vscode';
import { CopilotLogger } from './logger';

export class CopilotApiInterceptor {
    private logger: CopilotLogger;
    private isHooked = false;

    constructor(logger: CopilotLogger) {
        this.logger = logger;
    }

    /**
     * Try to hook into GitHub Copilot's API and fetch logs.
     */
    public tryHookCopilotApi(): boolean {
        try {
            const copilotExtension = vscode.extensions.getExtension('GitHub.copilot');
            if (!copilotExtension) {
                console.log('GitHub Copilot extension not found');
                return false;
            }

            console.log('GitHub Copilot extension found');
            this.isHooked = true;

            // Start fetching logs from Copilot logs
            this.startFetchingCopilotLogs();

            return true;
        } catch (error) {
            console.error('Failed to hook into Copilot API:', error);
            return false;
        }
    }

    /**
     * Continuously fetch logs from the Copilot logs using accessible data.
     */
    private startFetchingCopilotLogs(): void {
        setInterval(async () => {
            try {
                const copilotExtension = vscode.extensions.getExtension('GitHub.copilot');
                if (!copilotExtension?.isActive) {
                    console.log('GitHub Copilot extension is not active');
                    return;
                }

                // Attempt to access logs or other data
                const logs = await this.getCopilotLogs();
                if (!logs) {
                    console.log('No logs retrieved from Copilot');
                    return;
                }

                // Process the logs to extract user prompts
                const userPrompts = this.extractUserPromptsFromLogs(logs);
                for (const prompt of userPrompts) {
                    await this.logger.logUserInput('Copilot Logs', prompt);
                }
            } catch (error) {
                console.error('Error fetching Copilot logs:', error);
            }
        }, 5000); // Fetch every 5 seconds
    }

    /**
     * Attempt to retrieve logs from the Copilot extension.
     */
    private async getCopilotLogs(): Promise<string | null> {
        try {
            // Execute the command to open Copilot logs
            const logs = await vscode.commands.executeCommand<string>('github.copilot.openLogs');
            return logs || null;
        } catch (error) {
            console.error('Failed to retrieve Copilot logs:', error);
            return null;
        }
    }

    /**
     * Extract user prompts from the Copilot logs.
     */
    private extractUserPromptsFromLogs(logs: string): string[] {
        const userPrompts: string[] = [];
        const logLines = logs.split('\n');

        for (const line of logLines) {
            // Example: Extract lines that represent user input (adjust regex as needed)
            if (line.includes('User Input:')) {
                const prompt = line.split('User Input:')[1]?.trim();
                if (prompt) {
                    userPrompts.push(prompt);
                }
            }
        }

        return userPrompts;
    }
}
````

---

### Explanation of Changes

1. **`getCopilotLogs` Method**:
   - Executes the `github.copilot.openLogs` command to retrieve logs from the Copilot extension.
   - Handles cases where the command fails or returns no data.

2. **`extractUserPromptsFromLogs` Method**:
   - Parses the logs line by line to identify entries containing user prompts.
   - Uses a simple string matching or regex to extract the relevant text.

3. **Fallback Mechanism**:
   - If no logs are retrieved, the system logs a message and skips processing for that interval.

---

These changes ensure that user prompts are extracted from the Copilot logs or accessible data, even if the chat history API is unavailable. Let me know if further adjustments are needed!

