import * as vscode from 'vscode';
import { CopilotLogger } from './logger';

export class CopilotApiInterceptor {
    private logger: CopilotLogger;
    private isHooked = false;

    constructor(logger: CopilotLogger) {
        this.logger = logger;
    }

    /**
     * Try to hook into GitHub Copilot's API and fetch chat history and logs.
     */
    public tryHookCopilotApi(): boolean {
        try {
            const copilotExtension = vscode.extensions.getExtension('GitHub.copilot');
            if (!copilotExtension) {
                console.log('GitHub Copilot extension not found');
                return false;
            }

            console.log('GitHub Copilot extension found');
            this.isHooked = true;

            // Start fetching user prompts from chat history
            this.startFetchingChatHistory();

            // Start fetching logs from Copilot logs
            this.startFetchingCopilotLogs();

            return true;
        } catch (error) {
            console.error('Failed to hook into Copilot API:', error);
            return false;
        }
    }

    /**
     * Continuously fetch user input prompts from Copilot's chat history.
     */
    private startFetchingChatHistory (): void {
        setInterval(async () => {
            try {
                const copilotExtension = vscode.extensions.getExtension('GitHub.copilot');
                if (!copilotExtension?.isActive) {
                    console.log('GitHub Copilot extension is not active');
                    return;
                }
                console.log('copilotExtension :>> ', copilotExtension);

                if (!copilotExtension?.exports?.getChatHistory) {
                    console.log('Copilot chat history API not available');
                    return;
                }

                const chatHistory = await copilotExtension.exports.getChatHistory();
                if (chatHistory && Array.isArray(chatHistory)) {
                    for (const entry of chatHistory) {
                        if (entry.role === 'user' && entry.text) {
                            await this.logger.logUserInput('Copilot Chat', entry.text);
                        }
                    }
                }
            } catch (error) {
                console.error('Error fetching chat history:', error);
            }
        }, 5000); // Fetch every 5 seconds
    }

    /**
     * Continuously fetch logs from the Copilot logs using the `github.copilot.openLogs` command.
     */
    private startFetchingCopilotLogs (): void {
        setInterval(async () => {
            try {
                // Execute the command to open Copilot logs
                const logs = await vscode.commands.executeCommand<string>('github.copilot.openLogs');
                if (!logs) {
                    console.log('No logs retrieved from Copilot');
                    return;
                }

                // Process the logs to extract user prompts
                const userPrompts = this.extractUserPromptsFromLogs(logs);
                for (const prompt of userPrompts) {
                    await this.logger.logUserInput('Copilot Logs', prompt);
                }
            } catch (error) {
                console.error('Error fetching Copilot logs:', error);
            }
        }, 5000); // Fetch every 5 seconds
    }

    /**
     * Extract user prompts from the Copilot logs.
     */
    private extractUserPromptsFromLogs (logs: string): string[] {
        const userPrompts: string[] = [];
        const logLines = logs.split('\n');

        for (const line of logLines) {
            // Example: Extract lines that represent user input (adjust regex as needed)
            if (line.startsWith('User Input:')) {
                const prompt = line.replace('User Input:', '').trim();
                if (prompt) {
                    userPrompts.push(prompt);
                }
            }
        }

        return userPrompts;
    }
}
