// src/logger.ts - Updated with improved chat logging
import * as vscode from 'vscode';
import * as fs from 'fs';
import * as path from 'path';
import * as os from 'os';
import moment from 'moment';
import { ConfigManager } from './config';

export class CopilotLogger {
  private configManager: ConfigManager;
  private outputChannel: vscode.OutputChannel;
  // Recent prompts cache to avoid duplicates
  private recentPrompts: string[] = [];
  private readonly MAX_RECENT_PROMPTS = 20;

  constructor (configManager: ConfigManager) {
    this.configManager = configManager;
    this.outputChannel = vscode.window.createOutputChannel('Copilot Prompt Logger');
    this.outputChannel.appendLine('Logger initialized');
  }

  // Helper method for retry logic
  private async appendWithRetry (
    filepath: string,
    content: string,
    options: { maxRetries: number, delay: number }
  ): Promise<void> {
    let lastError: Error | null = null;

    for (let attempt = 0; attempt <= options.maxRetries; attempt++) {
      try {
        fs.appendFileSync(filepath, content, 'utf8');
        return; // Success
      } catch (error) {
        console.warn(`Append attempt ${attempt + 1} failed:`, error);
        lastError = error as Error;

        if (attempt < options.maxRetries) {
          // Wait before retry
          await new Promise(resolve => setTimeout(resolve, options.delay));
        }
      }
    }

    // All retries failed
    throw new Error(`Failed to append after ${options.maxRetries} retries: ${lastError?.message}`);
  }

  /**
   * Get the full path to the log file
   */
  public async getLogFilePath (): Promise<string> {
    // Try to use workspace folder first
    try {
      const workspaceFolders = vscode.workspace.workspaceFolders;
      if (workspaceFolders) {
        const rootPath = workspaceFolders[ 0 ].uri.fsPath;
        const now = new Date();
        const dateString = `${now.getFullYear()}-${(now.getMonth() + 1).toString().padStart(2, '0')}-${now.getDate().toString().padStart(2, '0')}`;

        // Create consistent folder structure
        const folderPath = path.join(rootPath, 'copilot-prompts');

        // Ensure directory exists
        if (!fs.existsSync(folderPath)) {
          fs.mkdirSync(folderPath, { recursive: true });
        }

        return path.join(folderPath, `prompt-log-${dateString}.md`);
      }
    } catch (error) {
      this.outputChannel.appendLine(`Workspace folder error: ${error}`);
    }

    // Fall back to user's home directory if no workspace
    const homeDir = os.homedir();
    const now = new Date();
    const dateString = `${now.getFullYear()}-${(now.getMonth() + 1).toString().padStart(2, '0')}-${now.getDate().toString().padStart(2, '0')}`;
    const folderPath = path.join(homeDir, '.vscode-copilot-logs');

    // Ensure directory exists
    if (!fs.existsSync(folderPath)) {
      fs.mkdirSync(folderPath, { recursive: true });
    }

    return path.join(folderPath, `prompt-log-${dateString}.md`);
  }

  /**
   * Initialize the log file if it doesn't exist
   */
  private async initLogFile (): Promise<string> {
    try {
      const logFilePath = await this.getLogFilePath();
      const dirPath = path.dirname(logFilePath);

      // Create directory with more robust error handling
      if (!fs.existsSync(dirPath)) {
        try {
          fs.mkdirSync(dirPath, { recursive: true });
          this.outputChannel.appendLine(`Created directory: ${dirPath}`);
        } catch (dirError) {
          this.outputChannel.appendLine(`Failed to create directory ${dirPath}: ${dirError}`);

          // Try an alternative location if workspace folder creation fails
          const homeDir = process.env.HOME || process.env.USERPROFILE;
          if (homeDir) {
            const altDir = path.join(homeDir, '.vscode', 'copilot-prompts');
            fs.mkdirSync(altDir, { recursive: true });
            this.outputChannel.appendLine(`Created alternative directory: ${altDir}`);
            return path.join(altDir, path.basename(logFilePath));
          }
          throw dirError;
        }
      }

      // Create log file with header if it doesn't exist
      if (!fs.existsSync(logFilePath)) {
        const header = `# GitHub Copilot Prompts Log - ${new Date().toLocaleDateString()}\nThis file contains logs of prompts sent to GitHub Copilot.\n\n`;
        try {
          fs.writeFileSync(logFilePath, header, 'utf8');
          this.outputChannel.appendLine(`Created new log file at: ${logFilePath}`);
        } catch (fileError) {
          this.outputChannel.appendLine(`Failed to create log file ${logFilePath}: ${fileError}`);
          throw fileError;
        }
      }

      return logFilePath;
    } catch (error) {
      console.error('Failed to initialize log file:', error);
      vscode.window.showErrorMessage(`Failed to initialize log file: ${error}`);
      throw error;
    }
  }

  /**
   * Append content to the log file
   */
  private async appendToLog (content: string): Promise<void> {
    if (!this.configManager.isEnabled()) {
      return;
    }

    if (!content || content.trim().length === 0) {
      this.outputChannel.appendLine('Skipping empty content');
      return;
    }

    try {
      const logFilePath = await this.initLogFile();

      try {
        // Try to append with retry logic
        await this.appendWithRetry(logFilePath, content, {
          maxRetries: 3,
          delay: 100
        });

        this.outputChannel.appendLine(`Logged content to: ${logFilePath}`);
        vscode.window.setStatusBarMessage(`Logged to ${path.basename(logFilePath)}`, 3000);
      } catch (appendError) {
        // If append fails after retries, try a different approach
        this.outputChannel.appendLine(`Append with retry failed: ${appendError}`);

        // Try to open file in write mode and append that way
        const currentContent = fs.existsSync(logFilePath) ?
          fs.readFileSync(logFilePath, 'utf8') : '';

        fs.writeFileSync(logFilePath, currentContent + content, 'utf8');
        this.outputChannel.appendLine('Logged content using write method');
      }
    } catch (error) {
      console.error('Failed to append to log file:', error);
      vscode.window.showErrorMessage(`Failed to append to log file: ${error}`);
    }
  }

  /**
   * Log a session start
   */
  public async logSessionStart (): Promise<void> {
    const timestamp = moment().format(this.configManager.getTimestampFormat());
    const content = `\n## Session Started: ${timestamp}\n\n`;
    await this.appendToLog(content);
  }

  /**
   * Log a session end
   */
  public async logSessionEnd (): Promise<void> {
    const timestamp = moment().format(this.configManager.getTimestampFormat());
    const content = `\n## Session Ended: ${timestamp}\n\n`;
    await this.appendToLog(content);
  }

  /**
   * Log a prompt to Copilot with improved filtering
   */
  public async logPrompt (fileName: string, context: string, promptText: string): Promise<void> {
    if (!promptText || promptText.trim().length === 0) {
      return; // Skip empty prompts
    }

    // Skip duplicates by keeping track of recently logged prompts
    if (this.isDuplicate(promptText)) {
      console.log('Skipping duplicate prompt');
      return;
    }

    // Clean up the prompt to exclude processing logs and metadata
    const cleanPrompt = this.cleanupPrompt(promptText);

    // Skip if still empty after cleanup or if it looks like a log entry rather than user input
    if (!cleanPrompt ||
      cleanPrompt.trim().length === 0 ||
      this.isSystemMessage(cleanPrompt)) {
      console.log('Skipping empty or system message');
      return;
    }

    const captureMode = this.configManager.getCaptureMode();

    // If capture mode is not userInputOnly, don't filter responses
    if (captureMode === 'userInputOnly' && this.looksLikeResponse(cleanPrompt)) {
      console.log('Skipping content that looks like a response');
      return;
    }

    // Save this prompt to prevent duplicates
    this.rememberPrompt(cleanPrompt);

    const timestamp = moment().format(this.configManager.getTimestampFormat());
    const relativePath = typeof fileName === 'string' ?
      vscode.workspace.asRelativePath(fileName, false) : 'Copilot Chat';

    // Format the log entry - keep it simple and focused on the actual prompt
    let content = `\n### User Prompt at ${timestamp}\n\n`;
    content += `Source: ${relativePath}\n\n`;

    // Only include context if it's meaningful and different from the prompt
    if (this.shouldIncludeContext(context, cleanPrompt)) {
      const truncatedContext = this.truncateIfNeeded(context, 500);
      content += `#### Context\n\n\`\`\`\n${truncatedContext}\n\`\`\`\n\n`;
    }

    // Add prompt section - this is the main focus
    content += `#### Input\n\n\`\`\`\n${cleanPrompt}\n\`\`\`\n\n`;

    // Add separator
    content += `---\n`;

    await this.appendToLog(content);
    this.outputChannel.appendLine(`Logged prompt: "${cleanPrompt.substring(0, 30)}..."`);
  }

  /**
   * Log a user input prompt - simplified version for chat messages
   */
  public async logUserInput (fileName: string, promptText: string): Promise<void> {
    if (!promptText || promptText.trim().length === 0) {
      return; // Skip empty prompts
    }

    // Avoid duplicates
    if (this.isDuplicate(promptText)) {
      this.outputChannel.appendLine('Skipping duplicate prompt');
      return;
    }

    this.rememberPrompt(promptText);

    const timestamp = moment().format(this.configManager.getTimestampFormat());
    const source = fileName || 'Copilot Chat';

    let content = `\n### User Prompt at ${timestamp}\n\n`;
    content += `Source: ${source}\n\n`;
    content += `\`\`\`\n${promptText}\n\`\`\`\n\n`;
    content += `---\n`;

    await this.appendToLog(content);
    this.outputChannel.appendLine(`Logged user input: "${promptText.substring(0, 30)}..."`);
  }

  /**
   * Log a chat message from Copilot Chat interface
   * Special format for chat interactions
   */
  public async logChatMessage (message: string): Promise<void> {
    if (!message || message.trim().length === 0) {
      return;
    }

    // Skip if duplicate
    if (this.isDuplicate(message)) {
      return;
    }

    this.rememberPrompt(message);

    const timestamp = moment().format(this.configManager.getTimestampFormat());

    // Use a special format for chat messages
    let content = `\n### Chat Message at ${timestamp}\n\n`;
    content += `\`\`\`\n${message}\n\`\`\`\n\n`;
    content += `---\n`;

    await this.appendToLog(content);
    this.outputChannel.appendLine(`Logged chat message: "${message.substring(0, 30)}..."`);
  }

  /**
   * Add diagnostic log for debugging
   */
  public async logDiagnostic (message: string): Promise<void> {
    this.outputChannel.appendLine(`[${new Date().toISOString()}] ${message}`);
  }

  // Utility methods for prompt processing

  private rememberPrompt (promptText: string): void {
    // Simple normalization for comparison
    const normalized = promptText.trim().toLowerCase();

    // Add to recent prompts and maintain max size
    this.recentPrompts.unshift(normalized);
    if (this.recentPrompts.length > this.MAX_RECENT_PROMPTS) {
      this.recentPrompts.pop();
    }
  }

  private isDuplicate (promptText: string): boolean {
    const normalized = promptText.trim().toLowerCase();
    return this.recentPrompts.includes(normalized);
  }

  private isSystemMessage (text: string): boolean {
    return text.includes('Session Started') ||
      text.includes('Session Ended') ||
      text.includes('Prompt at') ||
      text.startsWith('system:');
  }

  private shouldIncludeContext (context: string, promptText: string): boolean | string {
    return context &&
      context.trim() &&
      context !== promptText &&
      !context.includes('User Input') &&
      this.configManager.includeContext();
  }

  private truncateIfNeeded (text: string, maxLength: number): string {
    if (text.length <= maxLength) {
      return text;
    }
    return text.substring(0, maxLength) + '... [truncated]';
  }

  /**
   * Check if text looks like a Copilot response rather than user input
   */
  private looksLikeResponse (text: string): boolean {
    // Very specific patterns found in Copilot responses
    const responsePatterns = [
      'Updated content goes here',
      'The content has been further updated',
      'Additional content has been added',
      'This ensures that all necessary details',
      'Key takeaways and actionable insights',
      'Further enhancements have been made',
      'Here is the implementation'
    ];

    // If text matches any exact pattern from Copilot responses, skip it
    if (responsePatterns.some(pattern => text.includes(pattern))) {
      return true;
    }

    // More targeted checks for responses
    // Check length - most user prompts are short
    const isTooLong = text.length > 100;

    // Check for complete sentences that sound like responses
    const looksLikeComplete =
      (text.startsWith('The') && text.includes('.')) ||
      (text.startsWith('This') && text.includes('.')) ||
      (text.startsWith('Updates') && text.includes('.'));

    // Check for code block patterns
    const containsCodeBlocks =
      text.includes('```') ||
      (text.includes('{') && text.includes('}') && text.includes(';'));

    // Check for explanatory language
    const containsExplanatory =
      text.includes('function') ||
      text.includes('class') ||
      text.includes('method') ||
      text.includes('implements');

    // Simpler check focused on your specific use case
    return (isTooLong && looksLikeComplete) ||
      (isTooLong && containsCodeBlocks && containsExplanatory);
  }

  /**
   * Clean up prompt text to get just the user input
   */
  private cleanupPrompt (promptText: string): string {
    if (!promptText) {
      return '';
    }

    // Make a copy of the text
    let cleaned = promptText.toString();

    // Remove markdown formatting
    cleaned = cleaned.replace(/```[\w]*\n([\s\S]*?)```/g, '$1');

    // Remove common log artifacts
    const patternsToRemove = [
      /# GitHub Copilot Prompts Log.*$/gm,
      /## Session Started:.*$/gm,
      /## Session Ended:.*$/gm,
      /### .*Prompt at.*$/gm,
      /#### Context.*$/gm,
      /#### Input.*$/gm,
      /Source:.*$/gm,
      /File:.*$/gm,
      /---+/g,
      /\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2}/g
    ];

    for (const pattern of patternsToRemove) {
      cleaned = cleaned.replace(pattern, '');
    }

    // Remove empty lines and trim
    const lines = cleaned.split('\n').filter(line => line.trim() !== '');
    cleaned = lines.join('\n').trim();

    return cleaned;
  }
}

// src/config.ts
import path from 'path';
import * as vscode from 'vscode';

export class ConfigManager {
  /**
   * Configuration object
   */
  private config: vscode.WorkspaceConfiguration;

  constructor() {
    this.config = vscode.workspace.getConfiguration('copilotPromptLogger');
  }

  /**
   * Reload the configuration
   */
  public reloadConfig(): void {
    this.config = vscode.workspace.getConfiguration('copilotPromptLogger');
  }

  /**
   * Check if logging is enabled
   */
  public isEnabled(): boolean {
    return this.config.get<boolean>('enabled', true);
  }

  /**
   * Set whether logging is enabled
   */
  public setEnabled(enabled: boolean): Thenable<void> {
    return this.config.update('enabled', enabled, vscode.ConfigurationTarget.Global);
  }

  /**
   * Get the path to the log file
   */
  // In config.ts - Update the getLogFilePath method
  public getLogFilePath (): string {
    // Get the base path from configuration
    const basePath = this.config.get<string>('logFilePath', 'copilot-prompts/prompt-log.md');

    // Check if we should use date-based naming
    const useDateBasedNaming = this.config.get<boolean>('useDateBasedNaming', true);

    if (useDateBasedNaming) {
      const now = new Date();
      const dateString = `${now.getFullYear()}-${(now.getMonth() + 1).toString().padStart(2, '0')}-${now.getDate().toString().padStart(2, '0')}`;

      // Extract directory and filename parts
      const dirPart = path.dirname(basePath);
      const fileBase = path.basename(basePath, path.extname(basePath));
      const fileExt = path.extname(basePath) || '.md';

      return path.join(dirPart, `${fileBase}-${dateString}${fileExt}`);
    }

    return basePath;
  }

  /**
   * Get the timestamp format
   */
  public getTimestampFormat(): string {
    return this.config.get<string>('timestampFormat', 'YYYY-MM-DD HH:mm:ss');
  }

  /**
   * Get the number of context lines to include
   */
  public getContextLines(): number {
    return this.config.get<number>('includeContextLines', 5);
  }

  /**
   * Check if context should be included in logs
   */
  public includeContext (): boolean {
    return this.config.get<boolean>('includeContext', false);
  }

  /**
   * Get the capture mode (userInputOnly, inputAndResponse, all)
   */
  public getCaptureMode (): string {
    return this.config.get<string>('captureMode', 'userInputOnly');
  }

  // Add to config.ts class ConfigManager
  /**
   * Check if debug mode is enabled
   */
  public isDebugMode (): boolean {
    return this.config.get<boolean>('debugMode', false);
  }
}

// src/extension.ts
import * as vscode from 'vscode';
import * as fs from 'fs';
import { CopilotLogger } from './logger';
import { ConfigManager } from './config';
import { CopilotApiInterceptor } from './copilot-api';

let logger: CopilotLogger;
let configManager: ConfigManager;
let statusBarItem: vscode.StatusBarItem;
let copilotListenersDisposable: vscode.Disposable | null = null;

export function activate (context: vscode.ExtensionContext) {
	// Initialize the configuration manager
	configManager = new ConfigManager();

	// Initialize the logger
	logger = new CopilotLogger(configManager);

	// Log diagnostic info
	logger.logDiagnostic('Extension activated');

	// Register commands
	const enableLoggingCmd = vscode.commands.registerCommand('copilot-prompt-logger.enableLogging', () => {
		configManager.setEnabled(true);
		vscode.window.showInformationMessage('Copilot Prompt Logging is now enabled.');
		updateStatusBar();

		// Set up Copilot listeners when logging is enabled
		if (!copilotListenersDisposable) {
			logger.logDiagnostic('Setting up Copilot listeners');
			copilotListenersDisposable = setupCopilotListeners(context);
		}
	});

	const disableLoggingCmd = vscode.commands.registerCommand('copilot-prompt-logger.disableLogging', () => {
		configManager.setEnabled(false);
		vscode.window.showInformationMessage('Copilot Prompt Logging is now disabled.');
		updateStatusBar();

		// Dispose of Copilot listeners when logging is disabled
		if (copilotListenersDisposable) {
			copilotListenersDisposable.dispose();
			copilotListenersDisposable = null;
		}
	});

	const openLogFileCmd = vscode.commands.registerCommand('copilot-prompt-logger.openLogFile', async () => {
		const logFilePath = await logger.getLogFilePath();
		if (fs.existsSync(logFilePath)) {
			const doc = await vscode.workspace.openTextDocument(logFilePath);
			await vscode.window.showTextDocument(doc);
		} else {
			vscode.window.showErrorMessage('Copilot Prompts log file does not exist yet.');
		}
	});

	// Add debug command to check log file path
	const checkLogPathCmd = vscode.commands.registerCommand('copilot-prompt-logger.checkLogPath', async () => {
		const logFilePath = await logger.getLogFilePath();
		vscode.window.showInformationMessage(`Log file path: ${logFilePath}`);

		// Try to create a test log entry
		await logger.logPrompt('Debug', 'Test Context', 'This is a test prompt from checkLogPath command');
		vscode.window.showInformationMessage(`Test log written. Check file at: ${logFilePath}`);
	});

	// Register a session start in the log file
	logger.logSessionStart();

	// Setup text editor listeners
	if (configManager.isEnabled()) {
		// Setup editor text change monitoring
		copilotListenersDisposable = setupCopilotListeners(context);

		context.subscriptions.push(copilotListenersDisposable);
	}

	// Add Copilot API interceptor for fetching chat history
	const apiInterceptor = new CopilotApiInterceptor(logger);
	const hookSuccess = apiInterceptor.tryHookCopilotApi();

	if (hookSuccess) {
		logger.logDiagnostic('Copilot API hook successful');
	} else {
		logger.logDiagnostic('Could not hook into Copilot API directly');
	}

	// Register a command to capture input from clipboard
	const captureClipboardCmd = vscode.commands.registerCommand('copilot-prompt-logger.captureFromClipboard', async () => {
		try {
			const clipboardText = await vscode.env.clipboard.readText();
			if (clipboardText && clipboardText.length > 5) {
				await logger.logUserInput('Clipboard', clipboardText);
				vscode.window.showInformationMessage('Captured prompt from clipboard');
			} else {
				vscode.window.showInformationMessage('Clipboard is empty or too short');
			}
		} catch (error) {
			console.error('Failed to read clipboard:', error);
			vscode.window.showErrorMessage('Failed to read from clipboard');
		}
	});

	// Add configs, commands, and handlers to context subscriptions
	context.subscriptions.push(
		enableLoggingCmd,
		disableLoggingCmd,
		openLogFileCmd,
		checkLogPathCmd,
		captureClipboardCmd
	);

	// Register event listeners for configuration changes
	context.subscriptions.push(
		vscode.workspace.onDidChangeConfiguration(e => {
			if (e.affectsConfiguration('copilotPromptLogger')) {
				configManager.reloadConfig();
				updateStatusBar();
			}
		})
	);

	// Create a status bar item with context menu
	statusBarItem = vscode.window.createStatusBarItem(vscode.StatusBarAlignment.Right, 100);
	updateStatusBar();
	statusBarItem.command = 'copilot-prompt-logger.statusBarClick';
	statusBarItem.tooltip = 'Copilot Prompt Logger (click for options)';
	statusBarItem.show();
	context.subscriptions.push(statusBarItem);

	// Register status bar click command to show quick menu
	const statusBarClickCmd = vscode.commands.registerCommand('copilot-prompt-logger.statusBarClick', () => {
		// Show quick menu with common operations
		vscode.window.showQuickPick([
			'Toggle Logging',
			'Open Log File',
			'Capture from Clipboard',
			'Reconnect to Chat'
		], {
			placeHolder: 'Copilot Prompt Logger Options'
		}).then(selected => {
			if (selected === 'Toggle Logging') {
				const newValue = !configManager.isEnabled();
				configManager.setEnabled(newValue);
				updateStatusBar();
				vscode.window.showInformationMessage(`Copilot Prompt Logging is now ${newValue ? 'enabled' : 'disabled'}`);
			} else if (selected === 'Open Log File') {
				vscode.commands.executeCommand('copilot-prompt-logger.openLogFile');
			} else if (selected === 'Capture from Clipboard') {
				vscode.commands.executeCommand('copilot-prompt-logger.captureFromClipboard');
			} else if (selected === 'Reconnect to Chat') {
				vscode.commands.executeCommand('copilot-prompt-logger.reconnectWebsocket');
			}
		});
	});

	context.subscriptions.push(statusBarClickCmd);

	// Create new command to manually log a prompt
	let logPromptCommand = vscode.commands.registerCommand('copilot-prompt-logger.logPrompt', async () => {
		const prompt = await vscode.window.showInputBox({
			placeHolder: 'Enter your prompt',
			prompt: 'Log a prompt to the prompt log file'
		});

		if (prompt) {
			const activeEditor = vscode.window.activeTextEditor;
			const fileName = activeEditor ? activeEditor.document.fileName : 'Manual Entry';
			const context = activeEditor ? activeEditor.document.getText() : '';

			await logger.logPrompt(fileName, context, prompt);
			vscode.window.showInformationMessage('Prompt logged successfully!');
		}
	});

	context.subscriptions.push(logPromptCommand);

	// Add the 'logUserPrompt' command - simplified version for user-triggered logging
	const logUserPromptCmd = vscode.commands.registerCommand('copilot-prompt-logger.logUserPrompt', async () => {
		const prompt = await vscode.window.showInputBox({
			placeHolder: 'Enter your Copilot prompt',
			prompt: 'Log a prompt to the prompt log file'
		});

		if (prompt) {
			const editor = vscode.window.activeTextEditor;
			const fileName = editor ? editor.document.fileName : 'Manual Entry';

			// Use the simplified method for user inputs
			await logger.logUserInput(fileName, prompt);
			vscode.window.showInformationMessage('User prompt logged successfully!');
		}
	});

	context.subscriptions.push(logUserPromptCmd);
}

function updateStatusBar () {
	if (!statusBarItem) { return; }

	if (configManager.isEnabled()) {
		statusBarItem.text = "$(github-copilot) Logger: On";
		statusBarItem.backgroundColor = new vscode.ThemeColor('statusBarItem.warningBackground');
	} else {
		statusBarItem.text = "$(github-copilot) Logger: Off";
		statusBarItem.backgroundColor = undefined;
	}
}

export function deactivate () {
	// Log session end when extension is deactivated
	if (logger) {
		logger.logSessionEnd();
	}
}

// Improved function for monitoring editor changes that might be Copilot prompts
function setupCopilotListeners (_context: vscode.ExtensionContext): vscode.Disposable {
	const disposables: vscode.Disposable[] = [];

	// Listen for text document changes
	const textChangeListener = vscode.workspace.onDidChangeTextDocument(event => {
		if (!configManager.isEnabled()) { return; }

		const editor = vscode.window.activeTextEditor;
		if (!editor || event.document !== editor.document) { return; }

		// Skip log files and non-code files
		if (editor.document.fileName.includes('prompt-log') ||
			editor.document.fileName.endsWith('.md')) {
			return;
		}

		// Process each change
		for (const change of event.contentChanges) {
			// Look for likely human-authored prompts
			if (change.text &&
				change.text.trim().length > 5 &&
				change.text.trim().length < 200) {

				// Check for typical prompt patterns
				const isLikelyPrompt =
					// Questions are often prompts
					change.text.includes('?') ||
					// Command-like starts
					/^(add|create|update|write|generate|fix|implement|refactor|optimize|show)/i.test(change.text) ||
					// Single line inputs that look like instructions
					(change.text.split('\n').length <= 2 && /^[A-Z].*[.!?]$/.test(change.text));

				if (isLikelyPrompt) {
					logger.logUserInput(editor.document.fileName, change.text);
				}
			}
		}
	});

	disposables.push(textChangeListener);

	// Also listen for selection changes to capture multi-line selections
	const selectionChangeListener = vscode.window.onDidChangeTextEditorSelection(event => {
		if (!configManager.isEnabled()) { return; }

		const editor = event.textEditor;

		// Check if selection is significant and looks like it might be a prompt
		if (editor && editor.selection && !editor.selection.isEmpty) {
			const selectedText = editor.document.getText(editor.selection);

			// Only consider selections that are reasonably sized and look like prompts
			if (selectedText &&
				selectedText.length > 15 &&
				selectedText.length < 500 &&
				// Look for likely comment starts or question marks
				(selectedText.includes('?') ||
					selectedText.trim().startsWith('//') ||
					selectedText.trim().startsWith('/*') ||
					selectedText.trim().startsWith('#'))) {

				// We don't log immediately to avoid spam
				// Instead, we could add selection to a candidate list
				// and log if the user does something like press Enter or Ctrl+Enter
				// This would require more complex key binding tracking
			}
		}
	});

	return vscode.Disposable.from(...disposables);
}

