import * as vscode from 'vscode';
import { CopilotLogger } from './logger';

export class CopilotApiInterceptor {
    private logger: CopilotLogger;
    private isHooked = false;

    constructor(logger: CopilotLogger) {
        this.logger = logger;
    }

    /**
     * Try to hook into GitHub Copilot's API and fetch chat details.
     */
    public tryHookCopilotApi(): boolean {
        try {
            const copilotExtension = vscode.extensions.getExtension('GitHub.copilot');
            if (!copilotExtension) {
                console.log('GitHub Copilot extension not found');
                return false;
            }

            console.log('GitHub Copilot extension found');
            this.isHooked = true;

            // Start fetching chat details from "Show Chat Details"
            this.startFetchingChatDetails();

            return true;
        } catch (error) {
            console.error('Failed to hook into Copilot API:', error);
            return false;
        }
    }

    /**
     * Continuously fetch chat details from "Show Chat Details."
     */
    private startFetchingChatDetails (): void {
        setInterval(async () => {
            try {
                const copilotExtension = vscode.extensions.getExtension('GitHub.copilot');
                if (!copilotExtension?.isActive) {
                    console.log('GitHub Copilot extension is not active');
                    return;
                }

                // Execute the command to show chat details
                const chatDetails = await vscode.commands.executeCommand<any>('github.copilot.showChatDetails');
                if (!chatDetails || !Array.isArray(chatDetails)) {
                    console.log('No chat details retrieved from Copilot');
                    return;
                }

                // Process the chat details to extract user prompts
                const userPrompts = this.extractUserPromptsFromChatDetails(chatDetails);
                for (const prompt of userPrompts) {
                    await this.logger.logUserInput('Copilot Chat', prompt);
                }
            } catch (error) {
                console.error('Error fetching chat details:', error);
            }
        }, 5000); // Fetch every 5 seconds
    }

    /**
     * Extract user prompts from the chat details.
     */
    private extractUserPromptsFromChatDetails (chatDetails: any[]): string[] {
        const userPrompts: string[] = [];

        for (const entry of chatDetails) {
            if (entry.role === 'user' && entry.text) {
                userPrompts.push(entry.text.trim());
            }
        }

        return userPrompts;
    }
}
