import * as vscode from 'vscode';
import { CopilotLogger } from './logger';

export class CopilotApiInterceptor {
    private logger: CopilotLogger;
    private isHooked = false;

    constructor(logger: CopilotLogger) {
        this.logger = logger;
    }

    /**
     * Try to hook into GitHub Copilot's API and fetch chat details or user prompts.
     */
    public tryHookCopilotApi(): boolean {
        try {
            const copilotExtension = vscode.extensions.getExtension('GitHub.copilot');
            if (!copilotExtension) {
                console.log('GitHub Copilot extension not found');
                return false;
            }

            console.log('GitHub Copilot extension found');
            this.isHooked = true;

            // Check if the Copilot extension provides any useful APIs
            if (copilotExtension.exports && typeof copilotExtension.exports.getChatHistory === 'function') {
                console.log('Using Copilot API to fetch chat history');
                this.startFetchingChatHistory(copilotExtension.exports.getChatHistory);
            } else {
                console.log('No suitable API found in Copilot extension. Falling back to monitoring text editors.');
                this.startMonitoringTextEditors();
            }

            return true;
        } catch (error) {
            console.error('Failed to hook into Copilot API:', error);
            return false;
        }
    }

    /**
     * Fetch chat history using the provided API function.
     */
    private startFetchingChatHistory(getChatHistory: () => Promise<any[]>): void {
        setInterval(async () => {
            try {
                const chatHistory = await getChatHistory();
                if (chatHistory && Array.isArray(chatHistory)) {
                    for (const entry of chatHistory) {
                        if (entry.role === 'user' && entry.text) {
                            await this.logger.logUserInput('Copilot Chat', entry.text);
                        }
                    }
                }
            } catch (error) {
                console.error('Error fetching chat history:', error);
            }
        }, 5000); // Fetch every 5 seconds
    }

    /**
     * Fallback: Monitor active text editors for user input.
     */
    private startMonitoringTextEditors(): void {
        vscode.workspace.onDidChangeTextDocument(event => {
            const editor = vscode.window.activeTextEditor;
            if (!editor || event.document !== editor.document) {
                return;
            }

            for (const change of event.contentChanges) {
                if (change.text && change.text.trim().length > 5) {
                    this.logger.logUserInput(editor.document.fileName, change.text);
                }
            }
        });
    }
}
